import math

def solve_quadratic(a, b, c):
    discriminant = b**2 - 4*a*c
    if discriminant < 0:
        return None
    elif discriminant == 0:
        root = -b / (2*a)
        return (root,)
    else:
        root1 = (-b + math.sqrt(discriminant)) / (2*a)
        root2 = (-b - math.sqrt(discriminant)) / (2*a)
        return (root1, root2)

def main():
    a = 1
    b = -5
    c = 6

    roots = solve_quadratic(a, b, c)
    if roots is None:
        print("No real roots for the weather model.")
    else:
        print(f"Roots of the quadratic (weather model) are: {roots}")

if __name__ == "__main__":
    main()
